<script>
	import Complete from './Complete.svelte'
	import Connecting from './Connecting.svelte'
	import Finger from './Finger.svelte'
	import FirstScreen from './FirstScreen.svelte'
	import SecondScreen from './SecondScreen.svelte'
	import { gsap } from 'gsap'
	import { onMount, onDestroy } from 'svelte'

	export let className = ''

	export let viewBox = '0 0 1094 916'

	const INITIAL_DELAY = 1
	const FINGER_MOVEMENT = 1
	const FINGER_CLICK = 0.3
	const MOVEMNET_X = 205.05
	const SCREENS_TRANSITION = 0.5
	const SCREEN_VIEW = 1.5
	const CONNECTING_VIEW = 2

	let fingerRef
	// let firstScreen
	let secondScreen
	let isAnimating = false

	let firstScreenRef, firstScreenButton, firstScreenButtonLabel

	let secondScreenRef, secondScreenButton, secondScreenButtonLabel

	let connectingScreenRef

	let completeScreenRef

	let animations = []

	// Export startAnimation to parent component
	function startAnimation() {
		if (isAnimating) return
		isAnimating = true

		// Show FirstScreen for delay seconds
		// gsap.set(firstScreen, { opacity: 1 })

		// After delay, move finger to coordinates (373.06, 402.17)
		const delayedCall = gsap.delayedCall(INITIAL_DELAY, () => {
			// First: move to coordinates only
			const fingerAnim = gsap.to(fingerRef, {
				duration: FINGER_MOVEMENT,
				ease: 'power2.inOut',
				attr: {
					x: 361.05,
					y: 360.75
				},
				onComplete: () => {
					firstScreenClick()
				}
			})
			animations.push(fingerAnim)
		})
		animations.push(delayedCall)
	}

	function firstScreenClick() {
		const clickAnim = gsap.to(fingerRef, {
			duration: FINGER_CLICK,
			yoyo: true,
			repeat: 1,
			ease: 'power2.inOut',
			attr: {
				x: 373.06,
				y: 374.99,
				width: 384.84,
				height: 502.09
			},

			onComplete: () => {
				FirstToSecondScreenTransition()
			}
		})
		animations.push(clickAnim)

		const delayedCall = gsap.delayedCall(FINGER_CLICK / 2, () => {
			const buttonAnim = gsap.to(firstScreenButton, {
				duration: FINGER_CLICK / 2,
				ease: 'power2.inOut',
				attr: {
					fill: '#D9D9D9'
				}
			})
			animations.push(buttonAnim)

			const labelAnim = gsap.to(firstScreenButtonLabel, {
				duration: FINGER_CLICK / 2,
				ease: 'power2.inOut',
				attr: {
					fill: '#202221'
				}
			})
			animations.push(labelAnim)
		})
		animations.push(delayedCall)
	}

	function FirstToSecondScreenTransition() {
		const firstScreenAnim = gsap.to(firstScreenRef, {
			duration: SCREENS_TRANSITION,
			ease: 'power2.inOut',
			attr: {
				x: 238.22 - MOVEMNET_X
			},

			onComplete: () => {
				const delayedCall = gsap.delayedCall(SCREEN_VIEW, () => {
					secondScreenClick()
				})
				animations.push(delayedCall)
			}
		})
		animations.push(firstScreenAnim)

		const secondScreenAnim = gsap.to(secondScreenRef, {
			duration: SCREENS_TRANSITION,
			ease: 'power2.inOut',
			attr: {
				x: 238.22
			}
		})
		animations.push(secondScreenAnim)
	}

	function secondScreenClick() {
		const clickAnim = gsap.to(fingerRef, {
			duration: FINGER_CLICK,
			yoyo: true,
			repeat: 1,
			ease: 'power2.inOut',
			attr: {
				x: 373.06,
				y: 374.99,
				width: 384.84,
				height: 502.09
			},

			onComplete: () => {
				SecondToConnectingScreenTransition()
			}
		})
		animations.push(clickAnim)

		const delayedCall = gsap.delayedCall(FINGER_CLICK / 2, () => {
			const buttonAnim = gsap.to(secondScreenButton, {
				duration: FINGER_CLICK / 2,
				ease: 'power2.inOut',
				attr: {
					fill: '#D9D9D9'
				}
			})
			animations.push(buttonAnim)

			const labelAnim = gsap.to(secondScreenButtonLabel, {
				duration: FINGER_CLICK / 2,
				ease: 'power2.inOut',
				attr: {
					fill: '#202221'
				}
			})
			animations.push(labelAnim)
		})
		animations.push(delayedCall)
	}

	function SecondToConnectingScreenTransition() {
		const secondScreenAnim = gsap.to(secondScreenRef, {
			duration: SCREENS_TRANSITION,
			ease: 'power2.inOut',
			attr: {
				x: 238.22 - MOVEMNET_X
			},

			onComplete: () => {
				const delayedCall = gsap.delayedCall(CONNECTING_VIEW, () => {
					ConnectingToCompleteScreenTransition()
				})
				animations.push(delayedCall)
			}
		})
		animations.push(secondScreenAnim)

		const connectingScreenAnim = gsap.to(connectingScreenRef, {
			duration: SCREENS_TRANSITION,
			ease: 'power2.inOut',
			attr: {
				x: 238.22
			}
		})
		animations.push(connectingScreenAnim)

		const fingerAnim = gsap.to(fingerRef, {
			duration: FINGER_MOVEMENT,
			ease: 'power2.inOut',

			attr: {
				x: 505,
				y: 905.99
			}
		})
		animations.push(fingerAnim)
	}

	function ConnectingToCompleteScreenTransition() {
		const connectingScreenAnim = gsap.to(connectingScreenRef, {
			duration: SCREENS_TRANSITION,
			ease: 'power2.inOut',
			attr: {
				x: 238.22 - MOVEMNET_X
			}
		})
		animations.push(connectingScreenAnim)

		const completeScreenAnim = gsap.to(completeScreenRef, {
			duration: SCREENS_TRANSITION,
			ease: 'power2.inOut',
			attr: {
				x: 238.22
			},
			onComplete: () => {
				// Animation sequence completed, reset for next click
				isAnimating = false
			}
		})
		animations.push(completeScreenAnim)

		const fingerAnim = gsap.to(fingerRef, {
			duration: FINGER_MOVEMENT,
			ease: 'power2.inOut',

			attr: {
				x: 505,
				y: 905.99
			}
		})
		animations.push(fingerAnim)
	}

	function resetAnimation() {
		// Reset all screens to initial positions
		gsap.set(firstScreenRef, { attr: { x: 238.22 } })
		gsap.set(secondScreenRef, { attr: { x: 238.22 + MOVEMNET_X } })
		gsap.set(connectingScreenRef, { attr: { x: 238.22 + MOVEMNET_X } })
		gsap.set(completeScreenRef, { attr: { x: 238.22 + MOVEMNET_X } })

		// Reset finger to initial position
		gsap.set(fingerRef, {
			attr: {
				x: 505,
				y: 905.99,
				width: 479,
				height: 516
			}
		})

		// Reset button colors
		if (firstScreenButton) {
			gsap.set(firstScreenButton, { attr: { fill: '#202221' } })
		}
		if (firstScreenButtonLabel) {
			gsap.set(firstScreenButtonLabel, { attr: { fill: '#D9D9D9' } })
		}
		if (secondScreenButton) {
			gsap.set(secondScreenButton, { attr: { fill: '#202221' } })
		}
		if (secondScreenButtonLabel) {
			gsap.set(secondScreenButtonLabel, { attr: { fill: '#D9D9D9' } })
		}
	}

	export function hardReset() {
		// Kill all animations from array
		animations.forEach((anim) => anim.kill())
		animations = []

		// Reset to initial state
		isAnimating = false
		resetAnimation()

		// Start animation immediately
		startAnimation()
	}

	export function handleClick() {
		if (!isAnimating) {
			resetAnimation()
			startAnimation()
		}
	}

	onMount(() => {
		// Set initial state

		gsap.set(fingerRef, {
			attr: {
				x: 505,
				y: 905.99
			}
		})

		// Start animation sequence after a short delay
		setTimeout(() => {
			startAnimation()
		}, 500)
	})

	onDestroy(() => {
		// Kill all animations when component is destroyed
		animations.forEach((anim) => anim.kill())
		animations = []
	})
</script>

